script:
frappe.listview_settings['Sales Order'] = {
    button: {
        show: function(doc) {
            return true; 
        },
        get_label: function() {
            return __('ID'); 
        },
        get_description: function(doc) {
            return __('Details of {0}', [doc.name]); 
        },
        action: function(doc) {
            var order_id = doc.name;
            var order_type = doc.order_type;


            frappe.msgprint({
                title: __('Sales Order Details'),
                message: __('ID: {0}<br>Order Type: {1}', [order_id, order_type])
            });
        }
    },
    add_fields: ['order_type']
};

frappe.ui.form.on('Sales Order', {
    refresh: function(frm) {
        frm.add_custom_button(__('Show Customer Details'), function() {
            var customer_name = frm.doc.customer;

            if (customer_name) {
                frappe.call({
                    method: 'frappe.client.get',
                    args: {
                        doctype: 'Customer',
                        name: customer_name
                    },
                    callback: function(response) {
                        if (response.message) {
                            var customer = response.message;
                            var customer_details = `
                                <b>Customer Name:</b> ${customer.customer_name}<br>
                                <b>Customer Group:</b> ${customer.customer_group}<br>
                                <b>Territory:</b> ${customer.territory}<br>
                                <b>Email:</b> ${customer.email_id || 'N/A'}<br>
                                <b>Phone:</b> ${customer.phone || 'N/A'}
                            `;

                            frappe.msgprint({
                                title: __('Customer Details'),
                                message: customer_details,
                                wide: true
                            });
                        } else {
                            frappe.msgprint(__('No details found for the customer.'));
                        }
                    }
                });
            } else {
                frappe.msgprint(__('No customer specified in this Sales Order.'));
            }
        });
    }
});
